INSERT INTO `dvoo_buildings` VALUES (1010, 1, 'Wall Upgrade v1', 0, 'small_wallimprove', 0, 10, 30, 0, 'a:2:{i:59;i:6;i:60;i:4;}', 'Significantly increases the town''s defences.');
INSERT INTO `dvoo_buildings` VALUES (1011, 1, 'Pump', 0, 'small_water', 0, 0, 25, 0, 'a:2:{i:60;i:8;i:84;i:1;}', 'The pump unlocks all the construction projects involving water. It also increases the well''s underground reserves considerably.');
INSERT INTO `dvoo_buildings` VALUES (1020, 1, 'Water Purifier', 0, 'item_jerrycan', 1011, 0, 75, 0, 'a:4:{i:59;i:5;i:60;i:6;i:84;i:1;i:41;i:5;}', 'Converts water from Jerrycans found in the desert into drinkable water. (regular)');
INSERT INTO `dvoo_buildings` VALUES (1021, 1, 'Butcher', 0, 'item_meat', 1033, 0, 40, 2, 'a:2:{i:59;i:9;i:60;i:4;}', 'Here you can transform your little companions (dogs, cats, snakes...) into rations. Just think...some crazy people prefer beef!');
INSERT INTO `dvoo_buildings` VALUES (1023, 1, 'Great Pit', 0, 'small_gather', 1010, 10, 80, 0, 'a:1:{i:59;i:8;}', 'The Great Pit is a very effective defensive measure: a big hole surrounding the town. Also known as "the Brad", it is very practical in the long term, as it can be filled with all sorts of things to make it even more effective!');
INSERT INTO `dvoo_buildings` VALUES (1024, 1, 'Grater', 0, 'item_plate', 1010, 50, 40, 2, 'a:2:{i:60;i:15;i:41;i:5;}', 'Simple concept: sharp pieces of rusty metal fixed all along the tops of the town''s walls to make a kind of zombie grater.');
INSERT INTO `dvoo_buildings` VALUES (1025, 1, 'Defensive Focus', 0, 'item_meca_parts', 1033, 0, 50, 3, 'a:3:{i:159;i:7;i:160;i:8;i:41;i:6;}', 'Establishes an ingenious system for optimising the use of defensive materials (steel plate, etc.). This system increases the points value of each defensive object placed in the bank.');
INSERT INTO `dvoo_buildings` VALUES (1026, 1, 'Vegetable Plot', 0, 'item_vegetable_tasty', 1011, 0, 60, 1, 'a:3:{i:159;i:10;i:1;i:10;i:95;i:1;}', 'The vegetable plot can be used to grow vegetables that aren''t overly mouldy and fruit that starts to rot before it gets ripe. In short, basic rations, if we''re not being too fussy...');
INSERT INTO `dvoo_buildings` VALUES (1027, 1, 'Spiked Pit', 0, 'small_spears', 1010, 25, 60, 2, 'a:1:{i:59;i:20;}', 'One way to improve the Great Pit: fill it with sharpened wooden spikes, commonly known as "Brad the Impaler".');
INSERT INTO `dvoo_buildings` VALUES (1028, 1, 'Barbed Wire', 0, 'small_barbed', 1010, 5, 20, 0, 'a:1:{i:60;i:2;}', 'It''s even a little more basic than you might imagine - we''ve run out of wire...');
INSERT INTO `dvoo_buildings` VALUES (1029, 1, 'Drilling Rig', 0, 'small_water', 1011, 0, 60, 1, 'a:2:{i:159;i:7;i:160;i:2;}', 'A home made drilling rig which allows the well to be extended deeper into the ground, therefore significantly increasing available reserves...');
INSERT INTO `dvoo_buildings` VALUES (1030, 1, 'Eden Project', 0, 'small_eden', 1029, 0, 65, 3, 'a:3:{i:159;i:5;i:160;i:8;i:73;i:5;}', 'This is the last resort when there is no more water: rupture the water table with dynamite to extend the town''s reserves.');
INSERT INTO `dvoo_buildings` VALUES (1031, 1, 'Wall Upgrade v2', 0, 'small_wallimprove', 1010, 20, 40, 1, 'a:3:{i:159;i:9;i:160;i:6;i:41;i:6;}', 'It''s maybe too for tonight''s defences, but building a better wall allows for the addition of numerous upgrades');
INSERT INTO `dvoo_buildings` VALUES (1033, 1, 'Workshop', 0, 'small_refine', 0, 0, 25, 0, 'a:2:{i:59;i:10;i:60;i:8;}', 'The advancement of a town depends on the construction of a shabby workshop full of nondescript clutter. It allows resources to be converted and is a pre-requisite for the construction of other more advanced buildings.');
INSERT INTO `dvoo_buildings` VALUES (1035, 1, 'Scanner', 0, 'item_tagger', 1050, 0, 20, 2, 'a:4:{i:41;i:1;i:101;i:1;i:105;i:2;i:2;i:2;}', 'This homemade scanner, if used correctly, makes it twice as easy to obtain an estimation of the magnitude of the forthcoming attack (only requires half as many citizens to use it to obtain a solid estimate)! It is essential if the town is to be run efficiently.');
INSERT INTO `dvoo_buildings` VALUES (1036, 1, 'Metal Patches', 0, 'item_plate', 1010, 25, 55, 1, 'a:3:{i:159;i:1;i:160;i:3;i:41;i:2;}', 'Metallic supports to reinforce the weaker parts of the wall.');
INSERT INTO `dvoo_buildings` VALUES (1037, 1, 'Spiked Wall', 0, 'item_plate', 1036, 45, 35, 2, 'a:3:{i:59;i:5;i:60;i:20;i:134;i:1;}', 'A load of metal spikes on the ramparts can make for some zombie kebabs around nightfall...');
INSERT INTO `dvoo_buildings` VALUES (1039, 1, 'Boiling Fog', 0, 'small_waterspray', 1060, 35, 50, 0, 'a:4:{i:59;i:10;i:160;i:7;i:41;i:1;i:1;i:10;}', 'This machine takes a negligible amount of clean water, and sprays it out as fine boiling vapour. Originally used to clean the town''s streets, it is nonetheless ideal for melting your decomposing cohorts who come to visit every night.');
INSERT INTO `dvoo_buildings` VALUES (1040, 1, 'Saniflow Macerator', 0, 'small_grinder', 1060, 50, 55, 1, 'a:4:{i:159;i:2;i:160;i:10;i:64;i:2;i:84;i:2;}', 'Two huge slabs around the entrance way and a powerful piston system: when they are activated, they instantly crush anything in the way!');
INSERT INTO `dvoo_buildings` VALUES (1041, 1, 'Great Moat', 0, 'small_waterhole', 1023, 65, 50, 1, 'a:1:{i:1;i:20;}', 'Another upgrade for the Great Pit: fill it with water...');
INSERT INTO `dvoo_buildings` VALUES (1042, 1, 'Upgraded Map', 0, 'item_electro', 1050, 0, 15, 2, 'a:4:{i:101;i:1;i:60;i:1;i:105;i:2;i:2;i:2;}', 'This simple electronic gadget increases the level of detail visible on the map of the World Beyond: the exact number of zombies in each zone can be clearly seen. Invaluable if you don''t want to wander mindlessly into feeding time at the zombie zoo...');
INSERT INTO `dvoo_buildings` VALUES (1043, 1, 'Rock Cannon', 0, 'small_canon', 1046, 25, 60, 0, 'a:5:{i:159;i:5;i:160;i:5;i:84;i:2;i:101;i:1;i:134;i:3;}', 'An automatic turret which launches rocks towards the town gates at high velocity! It is activated at midnight and fires continuously for several minutes (if you were counting on sleeping in silence, you''re going to be disappointed)!');
INSERT INTO `dvoo_buildings` VALUES (1045, 1, 'Cremato-Cue', 0, 'item_hmeat', 1021, 0, 45, 3, 'a:2:{i:159;i:8;i:160;i:1;}', 'OK, do we all know what a crematorium is? Do we all also know what a barbecue is? Well then, it''s easy to guess what purpose a Cremato-Cue serves. In any case, it means we don''t die of starvation...well, some of us!');
INSERT INTO `dvoo_buildings` VALUES (1046, 1, 'Cannon Mounds', 0, 'small_dig', 1033, 15, 50, 0, 'a:3:{i:134;i:1;i:160;i:1;i:159;i:7;}', 'Multiple mounds of earth, reinforced with wooden beams. These are a pre-requisite for constructing powerful defence turrets...');
INSERT INTO `dvoo_buildings` VALUES (1047, 1, 'Railgun', 0, 'small_canon', 1046, 55, 30, 2, 'a:4:{i:160;i:10;i:84;i:1;i:101;i:1;i:41;i:4;}', 'A powerful piece of equipment which uses compressed air to fire balls of bent nails and other rusty metal. Perfect for making holes the size of a fist in whatever (or whomever) you like...');
INSERT INTO `dvoo_buildings` VALUES (1048, 1, 'Plate Gun', 0, 'small_canon', 1046, 60, 35, 1, 'a:5:{i:64;i:3;i:73;i:3;i:159;i:5;i:160;i:1;i:41;i:5;}', 'The Plate Gun fires heavy shards of steel plate straight towards the battlefield. It is butchery the likes of which you hope to never see again, but it is undeniably effective...');
INSERT INTO `dvoo_buildings` VALUES (1049, 1, 'War Mill', 0, 'item_wood_beam', 1033, 10, 15, 2, 'a:2:{i:159;i:2;i:160;i:1;}', 'Large wooden beams fixed to a rotating axle. It spins, really quickly.');
INSERT INTO `dvoo_buildings` VALUES (1050, 1, 'Watchtower', 0, 'item_tagger', 0, 5, 12, 0, 'a:2:{i:59;i:3;i:60;i:2;}', 'This tower, situated near the gates, gives an estimation of the magnitude of the forthcoming attack, allowing the citizens to be better prepared. It also unlocks all construction projects linked to emergency situations.');
INSERT INTO `dvoo_buildings` VALUES (1051, 1, 'Foundations', 0, 'small_building', 0, 0, 30, 0, 'a:2:{i:59;i:10;i:60;i:8;}', 'Foundations allow "Extreme Projects" to be completed (projects which take a long time to complete, but which have very powerful results).');
INSERT INTO `dvoo_buildings` VALUES (1052, 1, 'The Big Rebuild', 0, 'small_moving', 1051, 250, 800, 4, 'a:3:{i:159;i:15;i:160;i:7;i:134;i:5;}', 'This Extreme Project seeks to improve the town''s defences by restructuring the entire town. Lock down the houses, block the roads, install turrets on all the roofs... That''s the Big Rebuild!');
INSERT INTO `dvoo_buildings` VALUES (1053, 1, 'Mechanical Pump', 0, 'small_derrick', 1051, 0, 70, 2, 'a:2:{i:159;i:10;i:160;i:15;}', 'An extreme project which allows large quantities of water to be extracted from the deepest groundwater! This project adds +50 water rations to the well.');
INSERT INTO `dvoo_buildings` VALUES (1054, 1, 'Zombie Grater', 0, 'small_grater', 1010, 65, 60, 1, 'a:3:{i:60;i:20;i:64;i:3;i:41;i:5;}', 'Cover a large area of the ground in shards of sharpened metal and you get the world''s largest cheese grater.');
INSERT INTO `dvoo_buildings` VALUES (1055, 1, 'Old-school traps', 0, 'small_gather', 1010, 25, 65, 2, 'a:1:{i:59;i:15;}', 'Lots of holes surrounding the town which have been covered with branches. Now you just need to wait for something to fall into them...');
INSERT INTO `dvoo_buildings` VALUES (1056, 1, 'Ãœberwall', 0, 'item_plate', 1037, 80, 50, 2, 'a:4:{i:159;i:15;i:160;i:10;i:134;i:2;i:59;i:10;}', 'What is better than a wall ? ... A big wall.');
INSERT INTO `dvoo_buildings` VALUES (1057, 1, 'False Town', 0, 'small_falsecity', 1051, 200, 300, 3, 'a:3:{i:159;i:20;i:160;i:15;i:41;i:6;}', 'It''s a well known fact that zombies are a bit simple. If you construct a false second town, some of the attacking horde will try to attack it...');
INSERT INTO `dvoo_buildings` VALUES (1058, 1, 'Wooden Fencing', 0, 'small_fence', 1010, 20, 50, 0, 'a:2:{i:159;i:5;i:41;i:5;}', 'Wooden fences surrounding the town should (in theory)... slow down the zombie hordes significantly during the attack...');
INSERT INTO `dvoo_buildings` VALUES (1059, 1, 'Automatic Sprinkler', 0, 'small_sprinkler', 1060, 135, 50, 1, 'a:4:{i:159;i:7;i:160;i:15;i:1;i:20;i:84;i:1;}', 'Traditionally used in a garden, they also serve as a deadly defence against the Hordes... They kill lots of zombies, but just be sure to store plenty of water beforehand.');
INSERT INTO `dvoo_buildings` VALUES (1060, 1, 'Hydraulic Network', 0, 'item_firework_tube', 1011, 0, 40, 1, 'a:4:{i:60;i:5;i:160;i:5;i:84;i:2;i:41;i:5;}', 'Thanks to this network of pipes, you can build powerful water-based defences. Furthermore, this system adds a little extra water to the well.');
INSERT INTO `dvoo_buildings` VALUES (1061, 1, 'Faucet', 0, 'small_valve', 1051, 0, 130, 3, 'a:5:{i:159;i:6;i:60;i:10;i:160;i:3;i:39;i:1;i:41;i:4;}', 'Thanks to this small tap fixed to the well, any water wasted by the filtration system can be recovered (doesn''t require any additional water to be taken from the well). You can use this water to refill all water-based weapons (bombs, pistol, etc...) for FREE !');
INSERT INTO `dvoo_buildings` VALUES (1062, 1, 'Portal Lock', 0, 'small_door_closed', 0, 0, 16, 0, 'a:1:{i:60;i:2;}', 'A somewhat old-fashioned device which stops the gates being opened during the 20 minutes immediately before the attack. The gates still have to be closed manually.');
INSERT INTO `dvoo_buildings` VALUES (1064, 1, 'Predictor', 0, 'item_tagger', 1050, 0, 20, 1, 'a:2:{i:101;i:1;i:81;i:1;}', 'The predictor is a calculator of sorts, which estimates the magnitude of and works out details relating to the attack. In particular, it gives an estimation of TOMORROW''S attack!');
INSERT INTO `dvoo_buildings` VALUES (1065, 1, 'Factory', 0, 'small_factory', 1033, 0, 40, 1, 'a:3:{i:159;i:5;i:160;i:5;i:170;i:1;}', 'This upgraded version of the workshop reduces the cost of all conversions carried out in the workshop by 1 AP.');
INSERT INTO `dvoo_buildings` VALUES (1067, 1, 'Screaming Saws', 0, 'small_saw', 1033, 45, 65, 2, 'a:4:{i:41;i:3;i:160;i:2;i:60;i:5;i:81;i:3;}', 'Massive circular saws built outside which are activated by a clever system involving elastic bands and a cardboard tube... The noise made by the saws rotating is reminiscent of human screams...');
INSERT INTO `dvoo_buildings` VALUES (1069, 1, 'Reinforced Gates', 0, 'item_plate', 1062, 5, 35, 0, 'a:1:{i:59;i:3;}', 'Heavy steel plate fixed to the town gates to add extra zombie-proofing!');
INSERT INTO `dvoo_buildings` VALUES (1070, 1, 'Inner Wall', 0, 'item_plate', 1036, 55, 65, 3, 'a:2:{i:59;i:35;i:160;i:5;}', 'A wall within the existing wall provides an additional layer of protection against the nocturnal hordes...');
INSERT INTO `dvoo_buildings` VALUES (1072, 1, 'Searchtower', 0, 'small_gather', 1050, 0, 30, 1, 'a:3:{i:101;i:1;i:159;i:3;i:160;i:1;}', 'Each day, the search tower allows objects to be found in zones which were previously deemed "depleted". It detects unusual meteorological phenomena occurring outside and notes which area has been affected in the Chronicle.');
INSERT INTO `dvoo_buildings` VALUES (1073, 1, 'Water Turrets', 0, 'item_tube', 1011, 70, 60, 2, 'a:3:{i:160;i:10;i:1;i:60;i:84;i:1;}', 'This revolutionary defensive device fires powerful jets of water vapor into the ranks of zombies approaching our walls. With some additional adjustments, this will be an exceptionally powerful defence!');
INSERT INTO `dvoo_buildings` VALUES (1074, 1, 'Emergency Supplies', 0, 'status_terror', 1050, 10, 40, 0, 'a:2:{i:59;i:5;i:60;i:7;}', 'To prepare for any eventuality, it is sometimes necessary to build emergency defences which are not destined to last more than one attack. The Emergency Supplies system fulfils this need. Be sure not to waste resources hastily building temporary structures all over the camp!');
INSERT INTO `dvoo_buildings` VALUES (1015, 1, 'Catapult', 0, 'item_courroie', 1050, 0, 40, 1, 'a:4:{i:59;i:2;i:159;i:1;i:60;i:1;i:160;i:1;}', 'The catapult is a special machine which allows you fire all kinds of things into the World Beyond: very useful for preparing the ground for faraway expeditions!');
INSERT INTO `dvoo_buildings` VALUES (1075, 1, 'Building Registry', 0, 'item_rp_book2', 1033, 0, 15, 0, 'a:1:{i:170;i:1;}', 'With a little organisation and a solid table, it is possible to keep a detailed account of what is going on in the construction sites.');
INSERT INTO `dvoo_buildings` VALUES (1077, 1, 'Upgraded Catapult', 0, 'item_courroie', 1015, 0, 30, 2, 'a:4:{i:59;i:2;i:60;i:2;i:101;i:2;i:40;i:1;}', 'This upgrade improves the catapult by reducing the number of action points needed to launch items into the World Beyond.');
INSERT INTO `dvoo_buildings` VALUES (1013, 1, 'Spikes', 1, 'small_trap', 1074, 15, 12, 1, 'a:1:{i:59;i:6;}', 'Installing spiked poles around the town gives some extra defences at minimal cost, but they might just make the difference some nights...');
INSERT INTO `dvoo_buildings` VALUES (1012, 1, 'Wood Plating', 1, 'item_wood_plate', 1074, 10, 30, 0, 'a:1:{i:59;i:8;}', 'In case of emergency, it is often possible to make it through one more night by patching up the town walls with wood plating.');
INSERT INTO `dvoo_buildings` VALUES (1014, 1, 'Guerilla Traps', 1, 'small_trap', 1074, 35, 24, 1, 'a:3:{i:60;i:10;i:59;i:2;i:41;i:4;}', 'Setting a variety traps all over the place is a great way to take out large numbers of attacking zombies.');
INSERT INTO `dvoo_buildings` VALUES (1019, 1, 'Rubbish Heap', 1, 'small_dig', 1074, 5, 10, 0, 'a:2:{i:59;i:2;i:60;i:2;}', 'When the proverbial hits the fan, and there''s nothing else for it, just pile up everything that comes to hand, and pray that it slows the zombie horde down... The good thing is that you can build some really effective traps into it!');
INSERT INTO `dvoo_buildings` VALUES (1022, 1, 'Mount Killaman-Jaro', 1, 'small_dig', 1019, 60, 40, 1, 'a:1:{i:60;i:2;}', 'Once you have built a huge pile of rubbish, it is easy to cover it in skewers that are as rusty as they are deadly!');
INSERT INTO `dvoo_buildings` VALUES (1044, 1, 'Pits', 1, 'small_trap', 1074, 20, 20, 0, 'a:2:{i:74;i:3;i:60;i:2;}', 'This trap won''t kill the zombies, but it will stop them in their tracks!');
INSERT INTO `dvoo_buildings` VALUES (1068, 1, 'Bomb Factory', 1, 'small_tnt', 1074, 35, 20, 1, 'a:1:{i:73;i:3;}', 'Dynamite, zombies, blood.');
INSERT INTO `dvoo_buildings` VALUES (1034, 1, 'Automatic Piston Lock', 0, 'small_door_closed', 1062, 15, 25, 1, 'a:4:{i:59;i:10;i:160;i:3;i:41;i:4;i:84;i:1;}', 'This system uses powerful motorised pistons to automatically close the gates and prevent them from opening again half an hour before the attack.');
INSERT INTO `dvoo_buildings` VALUES (1066, 1, 'Bait', 1, 'small_meatbarbed', 1028, 50, 10, 2, 'a:1:{i:157;i:3;}', 'A lump of meat attached to a hook should distract at least a few zombies...');
INSERT INTO `dvoo_buildings` VALUES (1032, 1, 'Mines', 1, 'item_bgrenade', 1020, 110, 25, 2, 'a:4:{i:1;i:10;i:73;i:1;i:60;i:3;i:132;i:1;}', 'Rocket powder, detonators and clean water are all you need to make a big pile of putrefied flesh pulp. A powerful defence, but it can only be used once.');
INSERT INTO `dvoo_buildings` VALUES (1141, 1, 'Water Catcher', 1, 'item_tube', 1011, 0, 12, 1, 'a:2:{i:59;i:2;i:60;i:2;}', 'When it comes to water, every drop counts. This construction adds +2 rations to the well and can be rebuilt every day.');
INSERT INTO `dvoo_buildings` VALUES (1156, 1, 'Small Fence', 0, 'small_fence', 1010, 45, 50, 1, 'a:3:{i:59;i:20;i:159;i:5;i:41;i:2;}', 'Significantly increases the town''s defences.');
INSERT INTO `dvoo_buildings` VALUES (1076, 1, 'Gallows', 1, 'r_dhang', 1033, 0, 13, 0, 'a:2:{i:159;i:1;i:196;i:1;}', 'A cracking gallows for hanging undesirable types when you''d rather go a little further than just banishing them from the community... True craftsmanship is what your''re looking at here - this gallows isn''t going anywhere.');
INSERT INTO `dvoo_buildings` VALUES (1166, 1, 'Plywood', 0, 'item_plate', 1010, 15, 30, 1, 'a:2:{i:59;i:5;i:60;i:5;}', 'Plywood. You only picked up the thing because you had nothing better to do. You know it''s useless, but you did it anyway. Let''s go...');
INSERT INTO `dvoo_buildings` VALUES (1104, 1, 'Small CafÃ©', 1, 'small_cafet', 1033, 0, 6, 2, 'a:3:{i:59;i:2;i:1;i:1;i:95;i:1;}', 'When it''s a long time until meal time... There''s nothing like a solid plank of wood and some stale bread.');
INSERT INTO `dvoo_buildings` VALUES (1165, 1, 'Armour Plating v3', 0, 'item_plate', 1010, 30, 30, 2, 'a:2:{i:59;i:10;i:60;i:10;}', 'Basic, but thick. Can''t ask for much more really.');
INSERT INTO `dvoo_buildings` VALUES (1167, 1, 'Extrawall', 0, 'item_plate', 1010, 45, 30, 2, 'a:2:{i:59;i:15;i:60;i:15;}', 'It certainly wasn''t a genius who came up with this construction project, but in the end everyone agreed that another big wall is never a bad thing.');
INSERT INTO `dvoo_buildings` VALUES (1163, 1, 'Armour Plating', 0, 'item_plate', 1010, 25, 30, 1, 'a:2:{i:59;i:15;i:60;i:5;}', 'A basic defensive device, but nonetheless one which you''ll be happy to have when your ex-neighbour Kevo tries to snack on your delicious frontal lobe this evening.');
INSERT INTO `dvoo_buildings` VALUES (1142, 1, 'Mist Spray', 0, 'small_watercanon', 1011, 80, 40, 2, 'a:4:{i:59;i:5;i:60;i:5;i:160;i:5;i:1;i:15;}', 'A nice fine mist to water the beds of zombies growing near the gates');
INSERT INTO `dvoo_buildings` VALUES (1170, 1, 'Guard Tower', 0, 'small_watchmen', 1050, 15, 24, 2, 'a:4:{i:159;i:10;i:160;i:2;i:41;i:1;i:64;i:1;}', 'Building a big, beautiful tower and walkway really makes Hero guardians happy. This construction gives +10 defence points per Guardian in town and allows them to earn even more defence points by exchanging them for AP.');
INSERT INTO `dvoo_buildings` VALUES (1101, 1, 'Architect''s Study', 0, 'small_refine', 1075, 0, 75, 0, 'a:3:{i:159;i:10;i:69;i:1;i:51;i:1;}', 'When intelligent citizens meet in the evenings around the fire, great things are invented. Well, on the condition that they''ve built an Architecture Study. This building gives the town a (common) construction blueprint every day.');
INSERT INTO `dvoo_buildings` VALUES (1105, 1, 'Small Cemetary', 0, 'small_cemetery', 1033, 0, 36, 1, 'a:2:{i:59;i:10;i:41;i:1;}', 'Bring out your dead! For once, they''re going to be useful. Use them to summarily improve our defences. Each dead citizen brought to the cemetary gives +10 defence points to the town. Note: How and where the victim died makes no nevermind...');
INSERT INTO `dvoo_buildings` VALUES (1164, 1, 'Armour Plating v2', 0, 'item_plate', 1010, 25, 30, 1, 'a:2:{i:59;i:5;i:60;i:10;}', 'It''s not particularly advanced, but it does the job: makes you less likely to die.');
INSERT INTO `dvoo_buildings` VALUES (1150, 1, 'Sluice', 0, 'small_shower', 1011, 60, 50, 1, 'a:2:{i:59;i:10;i:1;i:15;}', 'The town''s waste water can still be used: all you need to do is to connect every citizen''s toilet to a cunning network of pipes and toboggans, and open the sluices at midnight. Just to check, everyone has their tent set up correctly, right?');
INSERT INTO `dvoo_buildings` VALUES (1159, 1, 'Slip ''n'' Slide', 0, 'small_wallimprove', 1010, 60, 40, 2, 'a:3:{i:134;i:1;i:1;i:10;i:95;i:6;}', 'Why did nobody think of this before? Instead of wasting your time on personal hygiene, use your soap to make the town walls slippy. Combined with the zombie grater, it''s ideal, except for the "noise".');
INSERT INTO `dvoo_buildings` VALUES (1113, 1, 'Scarecrows', 0, 'small_scarecrow', 1051, 15, 35, 0, 'a:2:{i:59;i:15;i:81;i:2;}', 'This field of scarecrows would work even better with citizens'' clothing, but that would require turning your town into a nudist colony.');
INSERT INTO `dvoo_buildings` VALUES (1109, 1, 'Henhouse', 0, 'small_chicken', 1033, 0, 40, 3, 'a:4:{i:59;i:5;i:159;i:5;i:84;i:1;i:42;i:3;}', 'If you forgot some time ago what a fried mushroom omelette with bacon and herbs tastes like, this building will be of no use to you. Well, on the plus side you''ll get some eggs. If you have your heart set on mushrooms, you''d best go and talk to the zombies...');
INSERT INTO `dvoo_buildings` VALUES (1160, 1, 'Vaporiser', 0, 'small_waterspray', 1010, 0, 50, 2, 'a:4:{i:60;i:10;i:160;i:2;i:41;i:2;i:84;i:1;}', 'A small hydraulic device designed to spray water vapour (and amusing chemicals too).');
INSERT INTO `dvoo_buildings` VALUES (1171, 1, 'Crows'' Nest', 0, 'small_watchmen', 1050, 10, 36, 3, 'a:3:{i:159;i:5;i:160;i:1;i:41;i:1;}', 'Not so much a tower as a really really long pole of which scouts can climb to the top to discover a building in the World Beyond (Once per day, per Hero).');
INSERT INTO `dvoo_buildings` VALUES (1143, 1, 'Water Filter', 0, 'item_jerrycan', 1020, 0, 50, 3, 'a:3:{i:60;i:10;i:101;i:2;i:204;i:1;}', 'Significantly increases the output of the purifier (high performance).');
INSERT INTO `dvoo_buildings` VALUES (1102, 1, 'Abbatoir', 0, 'small_slaughterhouse', 1033, 35, 40, 1, 'a:2:{i:160;i:10;i:134;i:2;}', 'An abattoir installed just in front of the town gates, and the entrance is facing the World Beyond. The problem generally lies in finding a volunteer every night to stand in it, waving their arms to entice the zombies in.');
INSERT INTO `dvoo_buildings` VALUES (1100, 1, 'Brutal Cannon', 1, 'small_canon', 1046, 25, 25, 0, 'a:2:{i:160;i:1;i:64;i:2;}', 'A machine to die for, literally. It needs to be reloaded every day.');
INSERT INTO `dvoo_buildings` VALUES (1149, 1, 'Shower', 0, 'small_shower', 1060, 0, 24, 2, 'a:4:{i:59;i:4;i:60;i:1;i:1;i:5;i:84;i:1;}', 'No, really, I''m being serious now, that... that... smell is properly unbearable. Take a shower. Now.');
INSERT INTO `dvoo_buildings` VALUES (1151, 1, 'Decon Shower', 0, 'small_shower', 1011, 35, 20, 3, 'a:1:{i:1;i:10;}', 'At the start, this was supposed to be a decontamination system. Upon use, however, it was seen to be an effective weapon against our pestilential friends. By adding a squirt of coconut shower gel, you can even perfume the rotting corpses you''re responsible for.');
INSERT INTO `dvoo_buildings` VALUES (1122, 1, 'Meat Locker', 0, 'small_fleshcage', 1051, 0, 40, 2, 'a:4:{i:159;i:1;i:60;i:8;i:41;i:2;i:128;i:1;}', 'Popular justice in all its glory! When a citizen is shunned they can be sent to the cage, which is judiciously located in plain sight, in front of the gates to the town. Their wailing and crying makes a great lure at midnight. Each shunned citizen sent to the cage earns temporary defence points for the town.');
INSERT INTO `dvoo_buildings` VALUES (1168, 1, 'Abject Panic', 1, 'status_terror', 1074, 30, 25, 0, 'a:3:{i:59;i:5;i:60;i:5;i:1;i:4;}', 'If the situation gets really desperate, you can decide to scream and panic. The advantage of this is that if all the survivors do it, this will disorientate the zombies (who really don''t need that kind of stress) and earns you sime virtual defence points... Yeah, it''s a load of nonsense.');
INSERT INTO `dvoo_buildings` VALUES (1155, 1, 'Ventilation System', 0, 'small_ventilation', 1062, 20, 24, 3, 'a:2:{i:60;i:8;i:41;i:1;}', 'This secret passage allows Heroes to enter and leave the town without using the gates!');
INSERT INTO `dvoo_buildings` VALUES (1103, 1, 'Defensive Thinktank', 0, 'item_shield', 1033, 0, 55, 4, 'a:2:{i:159;i:5;i:160;i:15;}', 'A major re-structuring of our defences to get the very best out of them (total town defences are increased by +10%).');
INSERT INTO `dvoo_buildings` VALUES (1152, 1, 'Divining Rocket', 0, 'small_rocketperf', 1011, 0, 100, 4, 'a:5:{i:159;i:5;i:160;i:5;i:84;i:1;i:132;i:1;i:73;i:1;}', 'A home-made rocket launcher fired into the ground: think about it! As the story goes, the inventor of this construction project was trying to invent the "rocket-jump". All he did was to re-invent "the crater". Nonetheless, this construction adds +60 water rations to the well.');
INSERT INTO `dvoo_buildings` VALUES (1127, 1, 'People''s Court', 0, 'small_court', 1051, 0, 12, 3, 'a:3:{i:59;i:6;i:160;i:5;i:170;i:1;}', 'It''s a well known fact that Heroes are always correct. Furthermore, to further cement this truth, any complaints registered against other citizens will count twice.');
INSERT INTO `dvoo_buildings` VALUES (1107, 1, 'Central Cafeteria', 0, 'small_cafet', 1033, 0, 20, 3, 'a:4:{i:159;i:5;i:160;i:1;i:170;i:2;i:95;i:2;}', 'The canteen increases the production of "kitchens" built by your town''s Heroes.');
INSERT INTO `dvoo_buildings` VALUES (1125, 1, 'Fortifications', 0, 'small_city_up', 1051, 0, 50, 3, 'a:3:{i:159;i:15;i:160;i:10;i:134;i:2;}', 'On the rare occasion a construction project benefits citizens directly, we won''t complain. All citizens'' houses will gain +4 defence points.');
INSERT INTO `dvoo_buildings` VALUES (1145, 1, 'Fertilizer', 0, 'item_digger', 1026, 0, 30, 3, 'a:4:{i:60;i:5;i:1;i:10;i:95;i:8;i:51;i:2;}', 'Significantly increases the yield of the vegetable plot.');
INSERT INTO `dvoo_buildings` VALUES (1114, 1, 'Garbage Dump', 0, 'small_trash', 1051, 0, 70, 0, 'a:3:{i:159;i:15;i:160;i:15;i:134;i:5;}', 'The cornerstone of every great town: a huge stinking dump all around the town. Admittedly, it''s not very aesthetically pleasing, but it allows you to convert everyday objects into effective temporary defences (for one night only).');
INSERT INTO `dvoo_buildings` VALUES (1124, 1, 'Lighthouse', 0, 'small_lighthouse', 1051, 0, 30, 3, 'a:3:{i:159;i:5;i:160;i:5;i:101;i:3;}', 'This beautiful big lighthouse will illuminate those long winter nights (is it any good in the summer?). All citizens will see their chances of surviving a camping trip improve.');
INSERT INTO `dvoo_buildings` VALUES (1119, 1, 'Wood Dump', 0, 'small_trash', 1114, 0, 30, 2, 'a:3:{i:59;i:5;i:60;i:5;i:41;i:1;}', 'Allows the destruction of wood at the dump.');
INSERT INTO `dvoo_buildings` VALUES (1169, 1, 'Frat House', 1, 'small_bamba', 1074, 75, 50, 3, 'a:3:{i:59;i:5;i:60;i:5;i:105;i:3;}', 'Partying is the best way to forget what''s going on outside, thankfully the zombies are there to ensure that it doesn''t get out of hand.');
INSERT INTO `dvoo_buildings` VALUES (1136, 1, 'Altar', 0, 'small_redemption', 1051, 0, 24, 3, 'a:3:{i:159;i:3;i:160;i:2;i:43;i:1;}', 'Just because the Crow is good and just, this shrine in its honour liberates all citizens that have been shunned from the town.');
INSERT INTO `dvoo_buildings` VALUES (1140, 1, 'Giant Sandcastle', 0, 'small_castle', 1051, 0, 300, 4, 'a:3:{i:159;i:15;i:160;i:10;i:1;i:30;}', 'If there''s one thing we''re not short of here, it''s sand. This wonder shines by the light of its uselessness alone: its construction earns a rare distinction for all the citizens still in town.');
INSERT INTO `dvoo_buildings` VALUES (1120, 1, 'Metal Dump', 0, 'small_trash', 1114, 0, 30, 2, 'a:2:{i:59;i:5;i:60;i:5;}', 'Allows the destruction of metal at the dump.');
INSERT INTO `dvoo_buildings` VALUES (1126, 1, 'Bonfire', 1, 'small_score', 1051, 30, 15, 3, 'a:2:{i:59;i:5;i:26;i:1;}', 'A large bonfire, set on the outskirts of the town to lead the zombies away from our flimsy houses.');
INSERT INTO `dvoo_buildings` VALUES (1123, 1, 'Animal Dump', 0, 'small_howlingbait', 1114, 0, 30, 2, 'a:1:{i:159;i:10;}', 'Allows the massacre of innocent animals at the dump.');
INSERT INTO `dvoo_buildings` VALUES (1128, 1, 'Ministry of Slavery', 0, 'small_slave', 1051, 0, 45, 4, 'a:3:{i:159;i:10;i:160;i:5;i:196;i:2;}', 'The Ministry for Slavers have stated that shunned citizens are allowed to work on construction projects. Furthermore, each AP they invest will contribute an additional 50% bonus to the construction site (eg: 6 AP invested by a shunned citizen, 9 AP are invested in the project).');
INSERT INTO `dvoo_buildings` VALUES (1115, 1, 'Dump Upgrade', 0, 'small_trash', 1114, 75, 120, 4, 'a:3:{i:159;i:10;i:160;i:10;i:1;i:20;}', 'Increases the yield of any object thrown in the dump by +1 temporary defence point.');
INSERT INTO `dvoo_buildings` VALUES (1144, 1, 'Grapeboom', 0, 'item_bgrenade', 1026, 0, 30, 2, 'a:3:{i:59;i:5;i:1;i:10;i:73;i:5;}', 'This is a major scientific breakthrough: by planting sticks of dynamite and watering them carefully, you can grow grenades!');
INSERT INTO `dvoo_buildings` VALUES (1158, 1, 'Concrete Reinforcement', 0, 'small_wallimprove', 1036, 50, 60, 3, 'a:2:{i:160;2;i:134;i:6;}', 'A concrete wall within the existing walls provides an additional layer of protection against the nocturnal hordes...');
INSERT INTO `dvoo_buildings` VALUES (1153, 1, 'Divining Rods', 0, 'small_waterdetect', 1011, 0, 130, 4, 'a:3:{i:159;i:5;i:101;i:5;i:160;i:10;}', 'For once, cutting-edge technology does something other than knock off vast quantities of zombies... This construction adds +100 water rations to the well.');
INSERT INTO `dvoo_buildings` VALUES (1173, 1, 'Reactor', 0, 'small_arma', 1051, 500, 100, 4, 'a:5:{i:160;i:15;i:134;i:2;i:2;10;i:101;i:4;i:39;i:1;}', 'This terrifying building came from a soviet submarine and sends powerful bolts of electricity arcing around the town with a deafening crackle. The only "hitch" is that it has to be repaired daily. If it is destroyed, the town and its surroundings are instantly vaporized, wiping everything off the map (where you''ll be). The label reads: soviet design, assembled in Â« Ð²ÑˆÐ¸Ð²Ñ‹Ð¹ Â».');
INSERT INTO `dvoo_buildings` VALUES (1132, 1, 'Labyrinth', 0, 'small_labyrinth', 1051, 110, 200, 3, 'a:4:{i:59;i:40;i:60;i:10;i:41;i:2;i:134;i:4;}', 'It''s a well known fact that zombies are a bit simple. So what better than a maze in front of the gates to lose a few of the horde during the attack? It''s really effective, but from now on, any citizen wishing to enter the town will need 1 AP to do so.');
INSERT INTO `dvoo_buildings` VALUES (1112, 1, 'Community Involvement', 0, 'small_strategy', 1033, 0, 60, 4, 'a:3:{i:159;15;i:160;i:15;i:41;i:3;}', 'The goal of this ambitious urban project is to better incorporate the defences of citizens'' homes into the town''s defences. With this construction, 80% of personal defences will count towards the town''s total, instead of the usual 40%.');
INSERT INTO `dvoo_buildings` VALUES (1133, 1, 'All or Nothing', 0, 'small_lastchance', 1051, 55, 150, 3, 'a:3:{i:159;i:15;i:160;i:10;i:41;i:4;}', 'Welcome to the last chance saloon! All objects in the bank will be destroyed and each one will provide 1 defence point.');
INSERT INTO `dvoo_buildings` VALUES (1172, 1, 'Free Dump', 0, 'small_trashclean', 1114, 0, 30, 3, 'a:5:{i:159;i:10;i:160;i:6;i:41;i:2;i:134;i:1;i:169;i:2;}', 'Reduces the cost of using the dump to 0 AP. Make sure not to build this if you still have some potential scoundrels in town...');
INSERT INTO `dvoo_buildings` VALUES (1137, 1, 'Giant BRD', 0, 'small_pmvbig', 1051, 0, 300, 4, 'a:2:{i:60;i:30;i:41;i:2;}', 'An frighteningly BRD on which the names of all the town residents are engraved. Pointing majestically towards the sky like... euh... A BRD. Nobody knows why, but someone has engraved, "PropriÃ©tÃ© de la Nuit Profonde" on the base. This wonder shines by the light of its uselessness alone: its construction earns a rare distinction for all the citizens still in town.');
INSERT INTO `dvoo_buildings` VALUES (1146, 1, 'Apple Tree', 0, 'small_appletree', 1011, 0, 30, 4, 'a:4:{i:1;i:10;i:159;i:1;i:95;i:3;i:74;i:2;}', 'An apple tree which looks more like a half-dead scrawny shrub but produces beautiful blue apples. Euh, hang on.. What?');
INSERT INTO `dvoo_buildings` VALUES (1117, 1, 'Weapons Dump', 0, 'small_trash', 1114, 0, 20, 3, 'a:2:{i:60;i:8;i:41;i:1;}', 'Allows the destruction of weapons at the dump.');
INSERT INTO `dvoo_buildings` VALUES (1157, 1, 'Third Wall', 0, 'item_plate', 1070, 80, 65, 1, 'a:3:{i:60;i:30;i:160;i:5;i:64;i:5;}', 'A third wall within the existing walls provides an additional layer of protection against the nocturnal hordes...');
INSERT INTO `dvoo_buildings` VALUES (1139, 1, 'Wonder Wheel', 0, 'small_wheel', 1051, 0, 300, 4, 'a:4:{i:160;i:5;i:41;i:5;i:134;i:3;i:1;i:20;}', 'It is indeed a vast and beautiful creation, you''ve also sunk the best part of our resources into the damn thing, but for some reason you''re proud of it. This wonder shines by the light of its uselessness alone: its construction earns a rare distinction for all the citizens still in town.');
INSERT INTO `dvoo_buildings` VALUES (1135, 1, 'Firework Foray', 0, 'small_fireworks', 1051, 0, 50, 4, 'a:5:{i:159;i:3;i:160;i:3;i:41;i:1;i:132;i:2;i:73;i:4;}', 'There''s nothing quite like a solid firework display to pass the long nights in the desert. This miniature version basically involves pointing the firing tubes at the areas surrounding the town and launching fireworks into the horde at midnight..');
INSERT INTO `dvoo_buildings` VALUES (1116, 1, 'Defence Dump', 0, 'small_trash', 1114, 0, 40, 3, 'a:2:{i:60;i:5;i:41;i:3;}', 'Increases the yield of any defensive objects thrown in the dump.');
INSERT INTO `dvoo_buildings` VALUES (1111, 1, 'Builders'' Merchant', 0, 'small_strategy', 1033, 0, 30, 4, 'a:3:{i:159;i:10;i:160;i:10;i:41;i:3;}', 'This major building provides the means to build more advanced houses.');
INSERT INTO `dvoo_buildings` VALUES (1138, 1, 'Crow Statue', 0, 'small_crow', 1051, 0, 300, 4, 'a:2:{i:159;i:35;i:74;i:3;}', 'Glory to the Crow! Glory be to their lenience and benevolence! Deliver us from spam and pardon our trolling as we forgive those who have trolled against us. This wonder shines by the light of its uselessness alone: its construction earns a rare distinction for all the citizens still in town.');
INSERT INTO `dvoo_buildings` VALUES (1134, 1, 'Air Strike', 1, 'small_rocket', 1051, 0, 50, 4, 'a:5:{i:60;i:5;i:41;i:1;i:1;i:10;i:132;i:4;i:73;i:1;}', 'Four magnificent rockets are launched at key targets around the town (north, south, east and west), killing all zombies found nearby in the World Beyond!');
INSERT INTO `dvoo_buildings` VALUES (1118, 1, 'Food Dump', 0, 'small_trash', 1114, 0, 20, 2, 'a:1:{i:59;i:15;}', 'Allows the destruction of food at the dump.');
INSERT INTO `dvoo_buildings` VALUES (1108, 1, 'Central Laboratory', 0, 'item_acid', 1033, 0, 30, 3, 'a:4:{i:159;i:3;i:160;i:10;i:41;i:3;i:95;i:10;}', 'The laboratory increases the production of "personal laboratories" built by your town''s Heroes.');
INSERT INTO `dvoo_buildings` VALUES (1130, 1, 'Cinema', 0, 'small_cinema', 1051, 0, 100, 4, 'a:5:{i:159;i:15;i:160;i:5;i:101;i:3;i:186;i:1;i:187;i:1;}', 'They''re showing Dawn of the Dead for the 636th time, you almost certainly missed the role played by one of the extras the last time you saw it, so watching it one more time won''t hurt.		With a little luck, it migth even make you laugh.');
INSERT INTO `dvoo_buildings` VALUES (1131, 1, 'Hot Air Balloon', 0, 'small_balloon', 1051, 0, 0, NULL, NULL, 'A large, round balloon floating in the sky, but just for as long as it takes for a "volunteer" citizen to take a note of everything he sees around the town. This building allows you to reveal everything hidden in the World Beyond.');
INSERT INTO `dvoo_buildings` VALUES (1162, 1, 'Gas Gun', 1, 'small_gazspray', 1160, 0, 0, NULL, NULL, 'Many choose to forget this, but zombies have brains of their own. Sometimes two, if they''re lucky. This is handy: the concentrate projected by this gun has an astonishing capacity for turning brains to sludge.');
INSERT INTO `dvoo_buildings` VALUES (1071, 1, 'Evolutive Wall', 0, 'item_home_def', 1036, 0, 0, NULL, NULL, 'By adding an iron frame to the walls, it''ll be easy to improve them each day, and without much effort!');
INSERT INTO `dvoo_buildings` VALUES (1161, 1, 'Acid Spray', 1, 'small_acidspray', 1160, 0, 0, NULL, NULL, 'This certainly isn''t going to improve the looks of the zombies at the gates.');
INSERT INTO `dvoo_buildings` VALUES (1110, 1, 'Infirmery', 0, 'small_infirmary', 1033, 0, 0, NULL, NULL, 'In the event of minor accidents or major trauma, the infirmery will welcome you with open arms. Well, on the condition that you''re able to carry out any procedures yourself, as the facility doesn''t come with medical personnel.');
INSERT INTO `dvoo_buildings` VALUES (1106, 1, 'Coffin Catapult', 0, 'small_coffin', 1105, 0, 0, NULL, NULL, 'Between two corpses, the one that''s moving has the best chance of eating you, trick the enemy by catapulting your corpses into the carnivorous crowd of shuffling scum! Each body earns +20 defence points instead of +10.');
INSERT INTO `dvoo_buildings` VALUES (1176, 1, 'Spirit Mirage', 0, 'small_spiritmirage', 1074, 0, 0, NULL, NULL, 'A spell cast by the shaman causing a disorienting mirage which causes a good number of zombies to get lost in the desert.');
INSERT INTO `dvoo_buildings` VALUES (1177, 1, 'Pluie BÃ©nite', 1, 'Holy Raincloud', 1074, 0, 0, NULL, NULL, 'A ritualistic dance round a lit fire, to which only the shaman know the technique, creates a small cloud which floats up through the air, and causes a shower of holy water to rain down on the zombie horde.');
INSERT INTO `dvoo_buildings` VALUES (1174, 1, 'XXL Voodoo Doll', 0, 'small_vaudoudoll', 1074, 0, 0, NULL, NULL, 'An agglomeration of damp tissue of over two metres in length, criss-crossed with all sorts of wires and needles. In the powerful hands of the shaman, this XXL doll decimates several zombies before becoming a shapeless, inanimate mass once again.');
INSERT INTO `dvoo_buildings` VALUES (1175, 1, 'Sword of Bokor', 0, 'small_bokorsword', 1074, 0, 0, NULL, NULL, 'Using this wicked looking guillotine and crudely constructed mannequin, the shaman can cut off the head of a pack-leader zombie at a distance, causing those who follow it to turn and wander back into the desert.');
INSERT INTO `dvoo_buildings` VALUES (1154, 1, 'Last-minute Lock', 0, 'small_door_closed', 1034, 0, 0, NULL, NULL, 'The gates close automatically at 22h59 instead of 22h30.');
INSERT INTO `dvoo_buildings` VALUES (1129, 1, 'No Holes Barred', 0, 'small_derrick', 1051, 0, 0, NULL, NULL, 'Because the drilling rig obviously cannot break though all rocks, it is sometimes necessary to send small animals strapped with dynamite down the holes. This project adds +150 water rations to the well.');
INSERT INTO `dvoo_buildings` VALUES (1178, 1, 'Shooting Gallery', 0, 'small_tourello', 1170, 0, 0, NULL, NULL, 'A fairground shooting alley, not the most mobile of affairs, it always goes down a storm on the ramparts, guaranteed to put a smile on a watchman''s face.');
INSERT INTO `dvoo_buildings` VALUES (1179, 1, 'Small Trebuchet', 0, 'small_catapult3', 1170, 0, 0, NULL, NULL, 'This beast-launcher lets you catapult your animal companions a considerable distance so that they can cause even more distress to the attacking horde. Furthermore, the noises made by wingless animals flying at speed is highly entertaining, which will help your fellow watchmen to relax a little.');
INSERT INTO `dvoo_buildings` VALUES (1180, 1, 'Miniature Armory', 0, 'small_armor', 1170, 0, 0, NULL, NULL, 'This armoury is a genuine top trump for watchmen. By building it, every weapon used during the night''s watcjh will have its effectiveness boosted by 20%!');
INSERT INTO `dvoo_buildings` VALUES (1181, 1, 'Swedish Workshop', 0, 'small_ikea', 1170, 0, 0, NULL, NULL, 'This small store allows all furniture being used on the night''s watch to become 20% more effective. Ah the Swedes... DIY masters of the universe!');
